// import headerLogo from '../images/Vector.svg';
import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';

function App() {
  // const [isEditPopupOpen, setIsEditPopupOpen] = React.useState(false);
  // const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  // const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  // function handleEditProfileClick() {
  //   setIsEditPopupOpen(true);
  // }
  // function handleAddPlaceClick() {
  //   setIsAddPlacePopupOpen(true);
  // }
  // function handleEditAvatarClick() {
  //   setIsEditAvatarPopupOpen(true);
  // }

  // function closeAllPopups() {
  //   setIsEditPopupOpen(false);
  //   setIsAddPlacePopupOpen(false);
  //   setIsEditAvatarPopupOpen(false);
  // }

  const handleEditAvatarClick = () => {
    document.querySelector('.popup_type_edit').classList.add('popup_open');
    document.querySelector('.profile__container').addEventListener('click', handleEditAvatarClick);
  };
  const handleEditProfileClick = () => {
    document.querySelector('.popup_type_edit').classList.add('popup_open');
    document.querySelector('.profile__change').addEventListener('click', handleEditProfileClick);
  };
  const handleAddPlaceClick = () => {
    document.querySelector('.popup_type_edit').classList.add('popup_open');
    document.querySelector('.profile__button').addEventListener('click', handleAddPlaceClick);
  };

  return (
    <div className="description">
      <div className="page">
        <Header />
        <Main>
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onClose={closeAllPopups}
        </Main>
        <Footer />

        <div className="popup popup_type_edit">
          <div className="popup__content">
            <button type="button" className="popup__close" id="popup-close"></button>
            <h3 className="popup__title">Редактировать профиль</h3>
            <form action="URL" className="popup__form" name="form" id="popup-form" novalidate>
              <input
                className="popup__input popup__input_type_name"
                type="text"
                name="name"
                placeholder="Жак-Ив Кусто"
                id="username"
                minlength="2"
                maxlength="40"
                required
              />
              <span id="username-error" className="popup__error" />
              <input
                type="text"
                name="about"
                placeholder="Исследователь океана"
                className="popup__input popup__input_type_name"
                id="userjob"
                minlength="2"
                maxlength="200"
                required
              />
              <span id="userjob-error" className="popup__error" />
              <button type="submit" className="popup__button">
                Сохранить
              </button>
            </form>
          </div>
        </div>

        <div className="popup popup_type_new">
          <div className="popup__content">
            <button type="button" className="popup__close" id="popup-close-new"></button>
            <h3 className="popup__title">Новое место</h3>
            <form action="URL" className="popup__form popup__form_add" id="popup-form-new" name="text" novalidate>
              <input
                type="text"
                name="name"
                placeholder="Название"
                className="popup__input popup__input_type_name"
                id="placeName"
                required
                minlength="2"
                maxlength="30"
              />
              <span id="placeName-error" className="popup__error"></span>
              <input
                type="url"
                name="link"
                placeholder="Ссылка на картинку"
                className="popup__input popup__input_type_name"
                id="link"
                required
              />
              <span id="link-error" className="popup__error"></span>
              <button type="submit" className="popup__button popup__button_disabled">
                Сохранить
              </button>
            </form>
          </div>
        </div>

        <div className="popup popup_type_update">
          <div className="popup__content">
            <button type="button" className="popup__close" aria-label="Закрыть"></button>
            <h3 className="popup__title">Обновить аватар</h3>
            <form className="popup__form popup__form_avatar" name="form" id="popupFormAvatar">
              <fieldset className="popup__fieldset">
                <input
                  className="popup__input popup__input_edit"
                  id="newAvatar"
                  name="link-avatar"
                  type="url"
                  value=""
                  placeholder="Ссылка на новый аватар"
                  required
                />
                <span className="popup__error" id="newAvatar-error"></span>
              </fieldset>
              <button type="submit" className="popup__button">
                Сохранить
              </button>
            </form>
          </div>
        </div>

        <div className="popup popup_type_image">
          <div className="popup__content popup__content_image">
            <button type="button" className="popup__close" id="popup-image-button"></button>
            <img src="#" alt="Картинка" className="popup__img" />
            <p className="popup__caption"></p>
          </div>
        </div>

        <div className="popup popup_type_delete">
          <div className="popup__content">
            <form className="popup__form popup__form_delete" name="form" id="popupFormDelete">
              <button type="button" className="popup__close" aria-label="Закрыть"></button>
              <h3 className="popup__title popup__title_delete">Вы уверены?</h3>
              <button type="submit" className="popup__button">
                Да
              </button>
            </form>
          </div>
        </div>

        <template className="template" id="template">
          <li className="element">
            <img src="#" alt="#" className="element__image" />
            <div className="element__caption">
              <h2 className="element__title"></h2>
              <div className="element__actions">
                <button type="button" className="element__button" id="like"></button>
                <p className="element__button_count">0</p>
              </div>
            </div>
            <button type="button" className="element__button_delete"></button>
          </li>
        </template>
      </div>
    </div>
  );
}

export default App;
